import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import './Breakdown.css';

const Breakdown = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  const largeTextVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: {
        duration: 0.8,
        ease: "easeOut"
      }
    }
  };

  return (
    <section className="breakdown section-large" ref={ref}>
      <div className="container">
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
        >
          <motion.h2 
            className="breakdown-title"
            variants={itemVariants}
          >
            This is Breakdown
          </motion.h2>
          
          <motion.p 
            className="breakdown-large-text"
            variants={largeTextVariants}
          >
            The science is settled. We are in an emergency of climate and nature. The world is past breaking point; the breakdown has begun...
          </motion.p>
          
          <div className="breakdown-grid">
            <motion.div 
              className="breakdown-item"
              variants={itemVariants}
            >
              <h3>The Role of Design</h3>
            </motion.div>
            
            <motion.div 
              className="breakdown-item"
              variants={itemVariants}
            >
              <h3>Time for Change</h3>
            </motion.div>
            
            <motion.div 
              className="breakdown-item"
              variants={itemVariants}
            >
              <h3>Act with Urgency</h3>
            </motion.div>
          </div>
          
          <motion.div 
            className="video-link-container"
            variants={itemVariants}
          >
            <a 
              href="https://youtu.be/XpnOe94eXdM" 
              target="_blank" 
              rel="noopener noreferrer"
              className="video-link"
            >
              View our D! Intro Video
            </a>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Breakdown;
