import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import './Header.css';

const Header = () => {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToDeclaration = () => {
    const element = document.getElementById('declaration');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <motion.header 
      className={`header ${isScrolled ? 'scrolled' : ''}`}
      initial={{ y: -100 }}
      animate={{ y: 0 }}
      transition={{ duration: 0.6, ease: "easeOut" }}
    >
      <div className="container">
        <div className="header-content">
          <div className="logo">
            <a href="#home" className="logo-text">Design Declares UK</a>
          </div>
          <nav className="nav">
            <div className="nav-desktop">
              <a href="#about" className="nav-link">About</a>
              <a href="#eight-acts" className="nav-link">8 Acts</a>
              <a href="#latest" className="nav-link">News</a>
              <a href="#signatories" className="nav-link">Signatories</a>
              <button 
                className="nav-link nav-btn"
                onClick={scrollToDeclaration}
              >
                Declare Now
              </button>
            </div>
            
            <button 
              className="mobile-menu-btn"
              onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            >
              <span></span>
              <span></span>
              <span></span>
            </button>
          </nav>
          
          {isMobileMenuOpen && (
            <div className="mobile-menu">
              <a href="#about" className="mobile-nav-link" onClick={() => setIsMobileMenuOpen(false)}>About</a>
              <a href="#eight-acts" className="mobile-nav-link" onClick={() => setIsMobileMenuOpen(false)}>8 Acts</a>
              <a href="#latest" className="mobile-nav-link" onClick={() => setIsMobileMenuOpen(false)}>News</a>
              <a href="#signatories" className="mobile-nav-link" onClick={() => setIsMobileMenuOpen(false)}>Signatories</a>
              <button 
                className="mobile-nav-link mobile-nav-btn"
                onClick={() => {
                  scrollToDeclaration();
                  setIsMobileMenuOpen(false);
                }}
              >
                Declare Now
              </button>
            </div>
          )}
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
