import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import './Newsletter.css';

const Newsletter = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1
  });

  const [email, setEmail] = useState('');
  const [newsletter, setNewsletter] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Newsletter signup:', { email, newsletter });
    alert('Thank you for subscribing! (This is a demo)');
    setEmail('');
    setNewsletter(false);
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.15,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  return (
    <section className="newsletter section-large" ref={ref}>
      <div className="container">
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate={inView ? "visible" : "hidden"}
        >
          <motion.h2 
            className="newsletter-title"
            variants={itemVariants}
          >
            The Design Declares Newsletter and Toolkit
          </motion.h2>
          
          <motion.div 
            className="newsletter-content"
            variants={itemVariants}
          >
            <div className="newsletter-image">
              <div className="placeholder-image">
                <span>Newsletter Image</span>
              </div>
            </div>
            
            <div className="newsletter-text">
              <p className="newsletter-description">
                Subscribe to the Design Declares UK newsletter to receive the latest news and updates. By signing up you will also gain access to The Design Declares Toolkit, a live and evolving Notion site co-created with our community. It is filled with the latest resources and methods to help you on your journey to climate-positive design.
              </p>
              
              <p className="newsletter-note">
                Every signatory to Design Declares will receive an access link to the Toolkit. If you are unable to declare emergency quite yet, you can still access the Toolkit - just register below.
              </p>
              
              <form className="newsletter-form" onSubmit={handleSubmit}>
                <div className="form-group">
                  <label htmlFor="newsletter-email">Email:*</label>
                  <input
                    type="email"
                    id="newsletter-email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  />
                </div>
                
                <div className="form-group checkbox-group">
                  <label>
                    <input
                      type="checkbox"
                      checked={newsletter}
                      onChange={(e) => setNewsletter(e.target.checked)}
                    />
                    I would like to be added to the Design Declares! newsletter and receive further updates.
                  </label>
                </div>
                
                <p className="privacy-link">
                  <a href="/privacy-policy" target="_blank" rel="noopener noreferrer">
                    View our Privacy Policy
                  </a>
                </p>
                
                <button type="submit" className="btn btn-primary">
                  Subscribe
                </button>
              </form>
            </div>
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default Newsletter;
